mapper.select:
select a.id, a.name, a.plan_phase, a.plan_status, a.create_time, a.priority, b.name create_by_name, a.handle_by, b.name handle_by_name from "plan" a join "user" b on b.id = a.create_by left join "user" c on c.id = a.handle_by where 1 = 1
and a.id = #{id}
and a.create_by = #{createBy}
and a.name like #{name}
and a.plan_status = '${planStatus}'
and a.plan_phase = '${planPhase}'
order by a.id desc

mapper.update:
update "plan" set
name = #{name}, project_id = #{projectId}, module_id = #{moduleId}, plan_id = #{planId}, describe = #{describe}, acceptance_criteria = #{acceptanceCriteria},
priority = #{priority}, plan_phase = '${planPhase}',  handle_by=#{handleBy}
where id = #{id}

update "plan" set plan_status = '${planStatus}'
plan_phase='已关闭'
where id = #{id}
